import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, View, Text, TextInput, Button, FlatList } from 'react-native';

import Start from './components/Start';
import Chat from './components/Chat';

// import react native gesture handler
import 'react-native-gesture-handler';

// import react Navigation
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';


// Create the navigator
const Stack = createStackNavigator();

//import firebase
const firebase = require('firebase');
require('firebase/firestore');

export default class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      lists: [],
    };

    const firebaseConfig = {
      apiKey: "AIzaSyCoeiYj2B6JajXBVTHYVGQkeWNaQTNFKBA",
      authDomain: "test-4a6e6.firebaseapp.com",
      projectId: "test-4a6e6",
      storageBucket: "test-4a6e6.appspot.com",
      messagingSenderId: "776200425549",
      appId: "1:776200425549:web:7ac43dbb39136df982cc04",
      measurementId: "G-NCYD292YNC"
    }

    if (!firebase.apps.length) {
      firebase.initializeApp(firebaseConfig);
    }
    this.referenceShoppingLists = firebase.firestore().collection('shoppinglists').doc('list2');

  }
  componentDidMount() {
    this.referenceShoppingLists = firebase.firestore().collection('shoppinglists');
    this.unsubscribe = this.referenceShoppingLists.onSnapshot(this.onCollectionUpdate)
  }
  onCollectionUpdate = (querySnapshot) => {
    const lists = [];
    // go through each document
    querySnapshot.forEach((doc) => {
      // get the QueryDocumentSnapshot's data
      var data = doc.data();
      lists.push({
        name: data.name,
        items: data.items.toString(),
      });
    });
    this.setState({
      lists,
    });
  }
  addList = () => {
    this.referenceShoppingLists.add({
      name: 'TestList',
      items: ['eggs', 'pasta', 'veggies'],
    });
  }
  render() {
    return (
      // 
      <View style={styles.container}>
        <FlatList
          data={this.state.lists}
          renderItem={({ item }) =>
            <Text style={styles.text}>{item.name}: {item.items}</Text>}
        />
        <Button onPress={() => { this.addList() }} title="Add something" />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingTop: 40,
  },
  item: {
    fontSize: 20,
    color: 'blue',
  },
  text: {
    fontSize: 30,
  }
});
