import React from 'react';
import { StyleSheet, View, Text, TextInput, Button, FlatList } from 'react-native';
import { GiftedChat, Bubble } from 'react-native-gifted-chat';
import { Platform, KeyboardAvoidingView } from 'react-native';
import AsyncStorage from "@react-native-async-storage/async-storage";

//import firebase
const firebase = require('firebase');
require('firebase/firestore');

export default class Chat extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            messages: [],
        }
        //config keys
        const firebaseConfig = {
            apiKey: "AIzaSyBQF4hVb36u56Q--Usb76HliDu3zD3YIxU",
            authDomain: "chatapp-b91f5.firebaseapp.com",
            projectId: "chatapp-b91f5",
            storageBucket: "chatapp-b91f5.appspot.com",
            messagingSenderId: "132737882655",
            appId: "1:132737882655:web:9a26378385be748f84132a",
            measurementId: "G-D61GNPCPCK"
        }

        if (!firebase.apps.length) {
            firebase.initializeApp(firebaseConfig);
        }
        this.referenceChatMessages = firebase.firestore().collection('messages');
    }

    onCollectionUpdate = (querySnapshot) => {
        const messages = [];
        // go through each document
        querySnapshot.forEach((doc) => {
            // get the QueryDocumentSnapshot's data
            let data = doc.data();
            messages.push({
                _id: data._id,
                text: data.text,
                createdAt: data.createdAt?.toDate(),
                user: {
                    _id: data.user._id,
                    name: data.user.name,
                    avatar: data.user.avatar || '',
                },
            });
        });
        this.setState({
            messages,
        });
    }
    async saveMessages() {
        try {
            await AsyncStorage.setItem('messages', JSON.stringify(this.state.messages));
        } catch (error) {
            console.log(error.message);
        }
    }

    async getMessages() {
        let messages = '';
        try {
            messages = await AsyncStorage.getItem('messages') || [];
            this.setState({
                messages: JSON.parse(messages)
            });
        } catch (error) {
            console.log(error.message);
        }
    }

    async deleteMessages() {
        try {
            await AsyncStorage.removeItem('messages');
        } catch (error) {
            console.log(error.message);
        }
    }


    // onSend(messages = []) {
    //     this.setState(
    //         (previousState) => ({
    //             messages: GiftedChat.append(previousState.messages, messages),
    //         }),
    //         () => {
    //             // Save messages locally with Async Storage
    //             this.saveMessages();
    //             // Call addMessage with last message in message state
    //             if (this.state.isConnected === true) {
    //                 this.addMessages(this.state.messages[0]);
    //             }
    //         }
    //     );
    // }

    //     
    componentDidMount() {
        this.getMessages();
    }
    onSend(messages = []) {
        this.setState(previousState => ({
            messages: GiftedChat.append(previousState.messages, messages),
        }), () => {
            const newMessage = this.state.messages[0];
            this.referenceChatMessages.add(newMessage);
            this.saveMessages();
        });
    }

    componentWillUnmount() {
        if (this.isConnected) {
            this.unsubscribe();
            this.authUnsubscribe();
        }
    }

    renderBubble(props) {
        return (
            <Bubble
                {...props}
                wrapperStyle={{
                    right: {
                        backgroundColor: '#000'
                    }
                }}
            />
        )
    }

    // renderBubble(props) {
    //     return (
    //         <Bubble
    //             {...props}
    //             wrapperStyle={{
    //                 left: {
    //                     backgroundColor: '#fafafa',
    //                 },
    //                 right: {
    //                     backgroundColor: '#2d7ecf',
    //                 },
    //             }}
    //         />
    //     )
    // }

    //     render() {
    //         let name = this.props.route.params.name;
    //         let color = this.props.route.params.color;
    //         if (color === '') {
    //             color = '#8A95A5';
    //         }
    //         return (
    //             <View style={[{ backgroundColor: color }, { flex: 1 }]}>
    //                 <GiftedChat
    //                     renderBubble={this.renderBubble.bind(this)}
    //                     messages={this.state.messages}
    //                     onSend={(messages) => this.onSend(messages)}
    //                     user={{ _id: this.state.user._id, name: this.state.user.name }}
    //                 />

    //                 {
    //                     Platform.OS === 'android' ? <KeyboardAvoidingView behavior="height" /> : null
    //                 }
    //             </View>
    //         )

    //     }
    // }
    render() {
        return (
            <View style={{ flex: 1, backgroundColor: 'green' }}>
                <GiftedChat
                    renderBubble={this.renderBubble}
                    messages={this.state.messages}
                    onSend={messages => this.onSend(messages)}
                    user={{
                        _id: 1,
                    }}

                />
            </View>
        )
    }
}
